const $formContact=document.querySelector("#contact-form");let state={};let nameInput=document.contactForm.name;nameInput.addEventListener('keyup',function(){toggleHelp('name',!1)});let emailInput=document.contactForm.email;emailInput.addEventListener('keyup',function(){toggleHelp('email',!1)});let subjectInput=document.contactForm.subject;subjectInput.addEventListener('keyup',function(){toggleHelp('subject',!1)});let messageInput=document.contactForm.message;messageInput.addEventListener('keyup',function(){toggleHelp('message',!1)});function encode(data){return Object.keys(data).map(key=>encodeURIComponent(key)+"="+encodeURIComponent(data[key])).join("&")}
const sendForm=event=>{if(event)event.preventDefault();document.contactForm.action.classList.add('is-loading');let isValid=!1;if(document.contactForm.honey){let input=document.contactForm.honey.value;if(input!==null&&input!==undefined&&input.length>0){toggleHelp('captcha',!0)}}
    const email=emailInput.value;if(email!==null&&email!==undefined){if(validateEmail(email)){let response=grecaptcha.getResponse();if(response===null||response===undefined||response.length===0){toggleHelp('captcha',!0)}else{toggleHelp('captcha',!1);let name=nameInput.value;let subject=subjectInput.value;let content=messageInput.value;if(name===null||name===undefined||name.length===0){toggleHelp('name',!0)}else if(subject===null||subject===undefined||subject.length===0){toggleHelp('subject',!0)}else if(content===null||content===undefined||content.length===0){toggleHelp('message',!0)}else{isValid=!0;try{let request=new XMLHttpRequest();request.open('POST',$formContact.getAttribute('action'),!0);request.setRequestHeader('Content-Type','application/x-www-form-urlencoded; charset=UTF-8');request.send(encode({"form-name":$formContact.getAttribute('name'),"name":name.toString(),"email":email.toString(),"subject":subject.toString(),"message":content.toString()}))}catch(e){alert("An unexpected error occurred while trying to send your message")}}}}else{toggleHelp('email',!0)}}else{toggleHelp('email',!0)}
    stopMailLoad(isValid);return!1};function stopMailLoad(expected){try{document.contactForm.action.classList.remove('is-loading')}catch(e){}
    return expected}
function toggleHelp(id,active){try{if(active){document.getElementById(id).classList.add('is-danger');document.getElementById(id+'-help').classList.add('is-active')}else{document.getElementById(id).classList.remove('is-danger');document.getElementById(id+'-help').classList.remove('is-active')}}catch(e){}}
function validateEmail(email){try{const re=/^([\w-]+(?:\.[\w-]+)*)@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$/i;return re.test(email.toLowerCase())}catch(e){return!1}}
