function setup(){let date=new Date();let month=date.getMonth()+1;let logo=document.getElementById('logo');let logoName='def';if(month===6){logoName='6'}
    if(logo!==null&&logo!==undefined){logo.setAttribute('src','/assets/img/brand/logo-'+logoName+'.svg');logo.setAttribute('onerror','this.onerror=null;'+'this.src=\'/assets/img/brand/logo-'+logoName+'.png\'')}
    let copy=document.getElementById('copyright-notice');if(copy!==null&&copy!==undefined){copy.innerHTML='Copyright '+date.getFullYear()+' Â© All Rights Reserved'}
    let years=document.getElementById('age-text');if(years!==null&&years!==undefined){years.innerHTML=(date.getFullYear()-1997).toString()}}
function loadParticles(){particlesJS.load('particles-bg','/assets/config/particles.json',function(){console.log('Particles ready! :D')})}
document.addEventListener('DOMContentLoaded',function(){let $navbarBurgers=Array.prototype.slice.call(document.querySelectorAll('.navbar-burger'),0);if($navbarBurgers.length>0){$navbarBurgers.forEach(function($el){$el.addEventListener('click',function(){let target=$el.dataset.target;let $target=document.getElementById(target);$el.classList.toggle('is-active');$target.classList.toggle('is-active')})})}
    setup();loadParticles()})