window.addEventListener('scroll',function(){let yPosition=(window.pageYOffset|document.body.scrollTop)+2;let portfolioTop=getElementTop('portfolio');let skillsTop=getElementTop('skills');let contactTop=getElementTop('contact');if(yPosition<portfolioTop){toggleLink('home')}else if(yPosition<skillsTop){toggleLink('portfolio')}else if(yPosition<contactTop){toggleLink('skills')}else{toggleLink('contact')}});function getElementTop(itemId){let bodyRect=document.body.getBoundingClientRect();let elementTop=bodyRect.top;if(itemId!==null&&itemId!==undefined){let element=document.getElementById(itemId);let bodyRect=document.body.getBoundingClientRect();if(element!==null&&element!==undefined){let elemRect=element.getBoundingClientRect();elementTop=elemRect.top-bodyRect.top}}
    return elementTop}
function scrollToItem(itemId){try{let elementTop=getElementTop(itemId);window.scroll({top:elementTop,left:0,behavior:'smooth'});toggleLink(itemId)}catch(err){}}
function toggleLink(itemId){let navLinks=document.getElementsByClassName('navbar-item');for(let i=0;i<navLinks.length;i++){navLinks[i].classList.remove('is-active')}
    document.getElementById(itemId+"-link").classList.add("is-active")}
function togglePortfolio(itemId){let portfolioFilters=document.getElementsByClassName('portfolio-filter');for(let i=0;i<portfolioFilters.length;i++){portfolioFilters[i].classList.remove('is-primary')}
    let portfolioSections=document.getElementsByClassName('portfolio-section');for(let i=0;i<portfolioSections.length;i++){let ele=portfolioSections[i];if(ele.id!=='portfolio-'+itemId&&ele.style.display!=='none'){fade(portfolioSections[i],'out',100)}}
    for(let j=0;j<2000;j++){}
    fade(document.getElementById("portfolio-"+itemId),'in',750,"flex");document.getElementById(itemId+"-filter").classList.add("is-primary")}